<?phpnamespace Corso\Http\Controllers;use Illuminate\Foundation\Bus\DispatchesCommands;use Illuminate\Routing\Controller as BaseController;use Illuminate\Foundation\Validation\ValidatesRequests;use Illuminate\Support\Facades\Response;use Input;abstract class Controller extends BaseController {    use DispatchesCommands,        ValidatesRequests;    /* Con estos methodos enviamos los mensajes de exito en cualquier controlador */    public function exito($data) {        return Response::json([                    'success' => TRUE,                    'message' => $data        ]);    }    /* Con estos methodos enviamos los mensajes de error en cualquier controlador */    public function errores($data) {        return Response::json([                    'success' => FALSE,                    'errors' => $data        ]);    }    function verificacion_fecha_corta() {        $mes_actual = date("m");        $mes_periodo = date('m') - 1;        if ($mes_periodo != 0) {            $year = date('Y');            $dia = date("d", (mktime(0, 0, 0, $mes_periodo + 1, 1, $year) + 1));            $mes = str_pad($mes_periodo, 2, '0', STR_PAD_LEFT);            $inicio = "$dia/$mes/$year";        }        if ($mes_actual != 0) {            $year = date('Y');            $dia = date("d", (mktime(0, 0, 0, $mes_actual + 1, 1, $year) - 1));            $final = "$dia/$mes_actual/$year";        }        $salida = array('inicio' => $inicio, 'final' => $final);        return $salida;    }    public function mes() {        $mes = date('m') - 1;        if ($mes == 0):            $mes = 12;        endif;        if ($mes < 10):            $mes = str_pad($mes, 2, '0', STR_PAD_LEFT);        endif;        $periodo = date('m/Y');        $year = date('Y');        $PeriodoAnt = $mes . '/' . $year;        $periodo = array($PeriodoAnt, $periodo);        return $periodo;    }    /**     *      * @return type     */    public function convertionObjeto() {        $datos = Input::get('data');        $DatosController = json_decode($datos);        return $DatosController;    }    public function convertionObjetoOne($data) {        $datos = Input::get($data);        $DatosController = json_decode($datos);        return $DatosController;    }}