<?phpnamespace Corso\Http\Controllers;use Illuminate\Foundation\Bus\DispatchesCommands;use Illuminate\Routing\Controller as BaseController;use Illuminate\Foundation\Validation\ValidatesRequests;use Illuminate\Support\Facades\Response;use Input;abstract class Controller extends BaseController {    use DispatchesCommands,        ValidatesRequests;    /* Con estos methodos enviamos los mensajes de exito en cualquier controlador */    public function exito($data) {        return Response::json([                    'success' => TRUE,                    'message' => $data        ]);    }    /* Con estos methodos enviamos los mensajes de error en cualquier controlador */    public function errores($data) {        return Response::json([                    'success' => FALSE,                    'errors' => $data        ]);    }     public function mes() {        $mes=  date('m')-1;         if($mes==0):             $mes = 12;         endif;                   if($mes < 10):             $mes = str_pad($mes, 2,'0',STR_PAD_LEFT);         endif;                 $periodo=date('m/Y');        $year= date('Y');        $PeriodoAnt= $mes.'/'.$year;               $periodo = array($PeriodoAnt,$periodo);        return $periodo;    }    /**     *      * @return type     */    public function convertionObjeto() {        $datos = Input::get('data');        $DatosController = json_decode($datos);        return $DatosController;    }}